template.value:
AnimatedBuilder(
  animation: $animationController$,
  child: $childWidget$,
  builder: (BuildContext context, Widget child) {
    return $buildWidget$;
  },
);

LayoutBuilder(
builder: (BuildContext context, BoxConstraints constraints) {
return $widget$;
},
),

showDialog<void>(
context: $context$,
barrierDismissible: $dismiss$, // false = user must tap button, true = tap outside dialog
builder: (BuildContext dialogContext){
return AlertDialog(
title: Text('$title$'),
content: Text('$content$'),
actions: <Widget>[
FlatButton(
child: Text('$buttonText$'),
onPressed: () {
Navigator.of(dialogContext).pop(); // Dismiss alert dialog
},
),
],
);
},
);
